import { Fragment } from 'react'
import { useParams, Route, Link, useMa } from 'react-router-dom';
import Components from '../components/comments/Comments';
import HighLightQuote from '../components/quotes/HighlightedQuote';

const DUMMY_QUOTES = [
    { id: 'q1', author: 'Ahmed', text: 'Learning new data' },
    { id: 'q2', author: 'Adel', text: 'new blog' },
]

const QuoteDetail = () => {
    const id = useParams();

    const quote = DUMMY_QUOTES.find(quote => quote.id === id.quoteId);

    if (!quote) {
        return <p>No quote found!</p>
    }

    return (
        <Fragment>
            <HighLightQuote text={quote.text} author={quote.author} />
            <Route path={`/quotes/${id.quoteId}`} exact>
                <div className='centered'>
                    <Link className='btn--flat' to={`/quotes/${id.quoteId}/comments`}>
                        Load Comments
                    </Link>
                </div>
            </Route>
            <Route path={`/quotes/${id.quoteId}/comments`}>
                <Components />
            </Route>

        </Fragment>
    );
}
export default QuoteDetail;
